mapper.select:
SELECT *
FROM urgent_form
WHERE mem_num = #{memNum}
ORDER BY care_date DESC, num DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*)
FROM urgent_form
WHERE mem_num = #{memNum}

SELECT *
FROM urgent_reservation
WHERE mem_num = #{memNum}
ORDER BY care_date DESC, res_num DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*)
FROM urgent_reservation
WHERE mem_num = #{memNum}

mapper.update:
UPDATE urgent_form
SET state = '매칭 완료'
WHERE num = #{formNum}

mapper.select:
SELECT COUNT(*)
FROM urgent_form
WHERE region = #{region}
